// AddPostForm.js
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addPost } from '../redux/actions';
import { TextField, Button } from '@mui/material';
import useStyles from './styles';

const AddPostForm = () => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState('');
  const classes = useStyles();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = { title, body: 'Lorem ipsum...', userId: 1, id: Date.now() };
    dispatch(addPost(newPost));
    // Save to local storage here
    setTitle('');
  };

  return (
    <div className={classes.addPostForm}>
      <h2>Add a New Post</h2>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Enter post title"
          variant="outlined"
          fullWidth
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ marginBottom: '15px' }} // Add margin bottom to create a gap
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.addPostButton}
          style={{ marginTop: '10px' }} // Add margin top to create a gap
        >
          Add Post
        </Button>
      </form>
    </div>
  );
};

export default AddPostForm;
