const AddPostForm = () => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState('');
  const classes = useStyles();

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = { title, body: 'Lorem ipsum...', userId: 1, id: Date.now() };

    // Dispatch the action to add post to Redux store
    dispatch(addPost(newPost));

    try {
      // Retrieve existing posts from local storage
      const storedPosts = JSON.parse(localStorage.getItem('posts')) || [];

      // Add the new post to the beginning of the array
      const updatedPosts = [newPost, ...storedPosts];

      // Save the updated posts to local storage
      localStorage.setItem('posts', JSON.stringify(updatedPosts));

      // Clear the title input
      setTitle('');
    } catch (error) {
      console.error('Error saving to local storage:', error);
    }
  };

  return (
    <div className={classes.addPostForm}>
      <h2>Create a Post</h2>
      <form onSubmit={handleSubmit}>
        <TextField
          label="What's on your mind?"
          variant="outlined"
          fullWidth
          multiline
          rows={4}
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ marginBottom: '15px' }}
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.addPostButton}
          style={{ marginTop: '10px' }}
        >
          Post
        </Button>
      </form>
    </div>
  );
};
